private VBox createGeneratorTab() {
    VBox vbox = new VBox(10);
    vbox.setPadding(new Insets(15));

    // 1. 创建输入区域
    GridPane inputGrid = new GridPane();
    inputGrid.setHgap(10);
    inputGrid.setVgap(10);

    // 添加各个时间字段的输入框和标签
    inputGrid.addRow(0, new Label("秒:"), secondField);
    inputGrid.addRow(1, new Label("分钟:"), minuteField);
    // 添加其他时间字段...

    // 2. 创建按钮区域
    HBox buttonBox = new HBox(10);
    Button generateBtn = new Button("生成Cron");
    generateBtn.setOnAction(e -> generateCron());
    buttonBox.getChildren().add(generateBtn);

    // 3. 结果区域
    resultArea.setEditable(false);
    resultArea.setWrapText(true);

    vbox.getChildren().addAll(
        new Label("Cron表达式生成器"),
        inputGrid,
        buttonBox,
        new Label("生成的Cron表达式:"),
        resultArea
    );

    return vbox;
}
