public class CronTool extends Application {

    private TextField secondsField = new TextField("0");
    private TextField minutesField = new TextField("0");
    private TextField hoursField = new TextField("0");
    private TextField dayOfMonthField = new TextField("*");
    private TextField monthField = new TextField("*");
    private TextField dayOfWeekField = new TextField("?");
    private TextField yearField = new TextField("*");

    private TextArea cronInput = new TextArea();
    private TextArea resultArea = new TextArea();
