    }

    private VBox createGeneratorTab() {
        VBox vbox = new VBox(10);
        vbox.setPadding(new Insets(15));

        // 输入区域
        GridPane grid = new GridPane();
        grid.setHgap(10);
        grid.setVgap(10);

        grid.add(new Label("秒 (0-59):"), 0, 0);
        grid.add(secondsField, 1, 0);
        grid.add(new Label("分 (0-59):"), 0, 1);
        grid.add(minutesField, 1, 1);
        grid.add(new Label("时 (0-23):"), 0, 2);
        grid.add(hoursField, 1, 2);
        grid.add(new Label("日 (1-31):"), 0, 3);
        grid.add(dayOfMonthField, 1, 3);
        grid.add(new Label("月 (1-12):"), 0, 4);
        grid.add(monthField, 1, 4);
        grid.add(new Label("周 (1-7 SUN=1):"), 0, 5);
        grid.add(dayOfWeekField, 1, 5);
        grid.add(new Label("年 (可选):"), 0, 6);
        grid.add(yearField, 1, 6);

        // 示例标签
        Label examples = new Label("示例: * = 所有值, ? = 不指定, 1-5 = 范围, 1,2,3 = 列表, */5 = 每5单位");
        examples.setStyle("-fx-text-fill: gray; -fx-font-size: 11;");

        // 生成按钮
        Button generateBtn = new Button("生成Cron表达式");
        generateBtn.setOnAction(e -> generateCron());

        // 结果显示
        TextField cronOutput = new TextField();
        cronOutput.setEditable(false);

        vbox.getChildren().addAll(grid, examples, generateBtn,
                new Label("生成的Cron表达式:"), cronOutput);

        return vbox;
    }

    private VBox createParserTab() {
        VBox vbox = new VBox(10);
        vbox.setPadding(new Insets(15));

        // 输入区域
        cronInput.setPromptText("输入Cron表达式，例如: 0 0 12 * * ?");
        cronInput.setPrefRowCount(3);

        Button parseBtn = new Button("解析Cron表达式");
        parseBtn.setOnAction(e -> parseCron());

        // 结果区域
        resultArea.setEditable(false);
        resultArea.setPrefRowCount(5);

        // 下次执行时间区域
        nextTimesArea.setEditable(false);
        nextTimesArea.setPrefRowCount(5);

        vbox.getChildren().addAll(
                new Label("输入Cron表达式:"),
                cronInput,
                parseBtn,
                new Label("表达式含义:"),
                resultArea,
                new Label("下次执行时间:"),
                nextTimesArea
        );

        return vbox;
    }

    private void generateCron() {
        String cron = String.format("%s %s %s %s %s %s %s",
                secondsField.getText(),
                minutesField.getText(),
                hoursField.getText(),
                dayOfMonthField.getText(),
                monthField.getText(),
                dayOfWeekField.getText(),
                yearField.getText());

        // 更新结果到解析标签页
        cronInput.setText(cron);

        // 在生成器标签页显示生成的cron表达式
        TabPane tabPane = (TabPane) resultArea.getScene().getRoot();
        VBox generatorTabContent = (VBox) tabPane.getTabs().get(0).getContent();
        TextField cronOutput = (TextField) generatorTabContent.getChildren().get(5);
        cronOutput.setText(cron);
    }// 确保列表有足够元素
    while (list.size() <= 5) {
        list.add(null); // 或其他默认值
    }
    Object item = list.get(5);


    private void parseCron() {
        String cronStr = cronInput.getText().trim();
        resultArea.clear();
        nextTimesArea.clear();

        try {
            CronExpression cron = new CronExpression(cronStr);
            resultArea.setText(getCronDescription(cronStr));

            // 计算下次执行时间
            Date now = new Date();
            StringBuilder times = new StringBuilder();
            for (int i = 0; i < 5; i++) {
                now = cron.getNextValidTimeAfter(now);
                if (now == null) break;
                times.append(now).append("\n");
                now = new Date(now.getTime() + 1000); // 移动到下一秒
            }
            nextTimesArea.setText(times.toString());

        } catch (ParseException e) {
            resultArea.setText("错误: " + e.getMessage());
        }
    }

    private String getCronDescription(String cron) {
        String[] parts = cron.split("\\s+");
        if (parts.length < 6) return "无效的Cron表达式";

        String[] labels = {"秒", "分", "时", "日", "月", "周", "年"};
        StringBuilder desc = new StringBuilder();

        for (int i = 0; i < Math.min(parts.length, 7); i++) {
            String part = parts[i];
            desc.append(labels[i]).append(": ");

            if (part.equals("*")) {
                desc.append("每").append(labels[i]);
            } else if (part.equals("?")) {
                desc.append("不指定");
            } else if (part.contains("-")) {
                String[] range = part.split("-");
                desc.append(range[0]).append("到").append(range[1]);
            } else if (part.contains(",")) {
                desc.append("第").append(part.replace(",", "和第"));
            } else if (part.contains("/")) {
                String[] step = part.split("/");
                desc.append("每隔").append(step[1]).append(labels[i]);
            } else {
                desc.append("第").append(part);
            }
            desc.append("\n");
        }
        return desc.toString();
    }

    public static void main(String[] args) {
        launch(args);
    }
}

